
// Import the Workbox scripts generated by next-pwa
import { precacheAndRoute } from 'workbox-precaching';
import { clientsClaim } from 'workbox-core';

// This is the most important part for a smooth update experience.
// It tells the new service worker to take control immediately.
self.skipWaiting();
clientsClaim();

// This line is automatically replaced by next-pwa with the list of files to cache.
precacheAndRoute(self.__WB_MANIFEST);

// --- Original Firebase Messaging Logic ---
// We keep this part to handle push notifications when the app is in the background.

// Give the service worker access to Firebase Messaging.
// Note that you can only use Firebase Messaging here, other Firebase services
// are not available in the service worker.
import { initializeApp } from 'firebase/app';
import { getMessaging, onBackgroundMessage } from 'firebase/messaging/sw';

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyBVOId-CRlTD6oKqvZ0CxKSFxObOoHEHd8",
  authDomain: "studio-5690519872-e0869.firebaseapp.com",
  projectId: "studio-5690519872-e0869",
  storageBucket: "studio-5690519872-e0869.appspot.com",
  messagingSenderId: "796529432751",
  appId: "1:796529432751:web:da147b13f407d67aaf9c5a",
  measurementId: "G-14976CYFEK"
};

initializeApp(firebaseConfig);

const messaging = getMessaging();

// Optional: Handle background messages here.
onBackgroundMessage(messaging, (payload) => {
  console.log('[firebase-messaging-sw.js] Received background message ', payload);
  
  const notificationTitle = payload.notification?.title || 'Broos 2.0';
  const notificationOptions = {
    body: payload.notification?.body,
    icon: '/icons/icon-192x192.png',
    data: {
        link: payload.data?.link || '/'
    }
  };

  self.registration.showNotification(notificationTitle, notificationOptions);
});


// --- Logic to handle notification clicks ---
self.addEventListener('notificationclick', (event) => {
  console.log('[Service Worker] Notification click Received.', event.notification);

  event.notification.close();

  const link = event.notification.data?.link || '/';
  
  event.waitUntil(
    clients.matchAll({
      type: "window",
      includeUncontrolled: true
    }).then((clientList) => {
      // Check if there's already a window/tab open with the app
      for (const client of clientList) {
        if (client.url === '/' && 'focus' in client) {
          // If so, focus it and navigate to the link
          return client.focus().then(c => c.navigate(link));
        }
      }
      // If not, open a new window
      if (clients.openWindow) {
        return clients.openWindow(link);
      }
    })
  );
});
